(()=>{const e=document.getElementById("nav-toggle"),t=document.getElementById("nav-toggle-label"),o=document.getElementById("side-nav-bar");e.addEventListener("change",(e=>{o.classList.toggle("active"),e.target.checked?(t.innerHTML='<i class="fas fa-times icon"></i>',document.body.style.top=`-${window.scrollY}px`,document.body.style.position="fixed"):(t.innerHTML='<i class="fas fa-bars icon"></i>',function(){const e=document.body.style.top;document.body.style.position="",document.body.style.top="",window.scrollTo(0,-1*parseInt(e||"0"))}())}))})();